mapper.select:
select a.id,a.NAME,a.success_num AS successNum ,a.failure_num AS failureNum,a.success_rate AS successRate,a.update_time AS updateTime,a.code AS code ,"business" AS tableFlag,a.is_finish AS isFinish,a.sys_task_id AS sysTaskId,b.id AS menuId from business a LEFT JOIN sys_permission b ON a.id = b.data_id WHERE b.type= "business"
AND a.project_id = #{projectId}
AND a.status = #{status}
AND a.name LIKE CONCAT('%', #{name}, '%')
AND a.create_by LIKE CONCAT('%', #{createBy}, '%')
ORDER BY a.code,a.name,a.create_time asc

