mapper.select:
	select 

	from dic_const d where 1=1 

      and d.id = #{id,jdbcType=VARCHAR}


      and d.dname  LIKE CONCAT('%', #{dname}, '%')


      and d.dvalue = #{dvalue,jdbcType=VARCHAR}


      and d.dtype = #{dtype,jdbcType=VARCHAR}


      and d.dparentCode = #{dparentCode,jdbcType=VARCHAR}


      and d.status = #{status,jdbcType=INTEGER}


      and d.remark = #{remark,jdbcType=VARCHAR}

mapper.delete:
delete from dic_const
where id IN

  #{id,jdbcType=INTEGER}
