mapper.select:
select

from model_script where 1=1

  and name =#{name}


  and tag =#{tag}


  and business_id =#{businessId}


  and type =#{businessId}

mapper.insert:
insert into model_script (id, name, tag, 
  business_id, type,retry,description, create_time,
  create_by, update_time, update_by,code
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{tag,jdbcType=INTEGER}, 
  #{businessId,jdbcType=INTEGER}, #{type,jdbcType=INTEGER},#{retry,jdbcType=INTEGER},#{description,jdbcType=VARCHAR},#{createTime,jdbcType=TIMESTAMP},
  #{createBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{code}
  )

mapper.delete:
delete from model_script
where id IN

  #{id,jdbcType=INTEGER}
