  #{id,jdbcType=INTEGER}

mapper.select:
select 
from testcase_report where testcast_id = #{id}

  and phone_num = #{phoneNum}

SELECT p.name as projectName, h.serial_number as serialNumber, b.id As testCaseReportId, h.id AS testcastId, b.province AS province, b.log AS log, b.execute_host as executeHost, b.is_finish AS isFinish, b.excute_result AS excuteResult, b.pass_status AS passStatus, b.defect_description AS defectDescription, b.snapshot_location AS snapshotLocation, b.video_location AS videoLocation, b.create_time AS excuteTime, b.phone_num AS phoneNum, e. NAME AS scriptName, d.`name` AS businessName, a.`name` AS testcastName, a.excute_num AS excuteNum, 'web' AS type FROM testcase a JOIN (SELECT * FROM testcase WHERE old_testcase_id = 0
AND name LIKE CONCAT('%', #{testcastName},'%')
) h ON a.old_testcase_id = h.id
JOIN testcase_report b ON a.id = b.testcast_id JOIN business d ON d.id = h.business_id JOIN model_script e ON e.id = a.script_id join project p on d.project_id = p.id WHERE a.sys_task_id IS NULL
AND d.name LIKE CONCAT('%', #{businessName},'%')
AND p.name LIKE CONCAT('%', #{projectName},'%')
AND b.province LIKE CONCAT('%',#{province},'%')
AND b.excute_result = #{excuteResult}
AND a.survey_task_id = #{surveyTaskId}
AND a.survey_task_id IS NULL
AND a.old_testcase_id = #{testcastId}
AND a.old_testcase_id IN
#{testcastId,jdbcType=INTEGER}
AND a.excute_num = #{excuteNum}
AND b.phone_num LIKE CONCAT('%',#{phoneNum},'%')
AND b.snapshot_location is not null and b.snapshot_location != ''
AND b.id IN
#{testCaseReportId,jdbcType=INTEGER}
