mapper.select:
	select

	from timed_task where 1=1

		AND job_name LIKE CONCAT('%', #{jobName }, '%')


		AND group_name LIKE CONCAT('%', #{groupName }, '%')


	    AND id = #{id,jdbcType=INTEGER}


        AND status = #{status,jdbcType=INTEGER}


        AND test_case_type = #{testCaseType,jdbcType=VARCHAR}


        AND test_case_id = #{testCaseId,jdbcType=INTEGER}


        AND type = #{type,jdbcType=INTEGER}


        AND cron = #{cron,jdbcType=VARCHAR}


        AND trigger_group_name = #{triggerGroupName,jdbcType=VARCHAR}


        AND trigger_name = #{triggerName,jdbcType=VARCHAR}


        AND create_by = #{createBy,jdbcType=VARCHAR}


        AND create_time = #{createTime,jdbcType=TIMESTAMP}


        AND update_by = #{updateBy,jdbcType=VARCHAR}


        AND update_time = #{updateTime,jdbcType=TIMESTAMP}

	ORDER BY id DESC

mapper.update:
update timed_task


    status = #{status},


    update_by = #{updateBy,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP}


where test_case_id = #{testCaseId,jdbcType=INTEGER} and test_case_type= #{testCaseType,jdbcType=VARCHAR}

mapper.delete:
delete from timed_task
where id IN

  #{id,jdbcType=INTEGER}

OR old_testcase_id IN

  #{id,jdbcType=INTEGER}
