mapper.select:
SELECT a.*, IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 2 AND ( a.avg_click_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_click_num <= CAST( `end` AS DECIMAL) ) LIMIT 1 ),100) as 'click_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 3 AND ( a.avg_input_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_input_num <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'input_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 4 AND ( a.avg_msg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_msg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'msg_consume_time_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 1 AND ( a.avg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'consume_time_score', success_rote AS 'score', "1" AS 'source' FROM ( SELECT c.old_case_id as 'testcase_id', c.case_name as 'testcase_name', c.survey_task_id, (0+CAST(IFNULL(a.avg_click_num, 0) AS CHAR)) as 'avg_click_num', (0+CAST(IFNULL(a.avg_input_num, 0)AS CHAR)) as 'avg_input_num', (0+CAST(IFNULL(a.avg_msg_consume_time, 0)AS CHAR)) as 'avg_msg_consume_time', (0+CAST(IFNULL(a.avg_consume_time, 0)AS CHAR)) as 'avg_consume_time', (0+CAST(IFNULL(b.success_rote, 0)AS CHAR)) as 'success_rote' FROM app_case as c LEFT JOIN ( SELECT a.success_num / ( a.success_num + a.failure_num ) * 100 as 'success_rote', a.testcase_id, survey_task_id FROM ( SELECT SUM( CASE a.execute_result WHEN 0 THEN 1 ELSE 0 END ) as 'success_num', SUM( CASE a.execute_result WHEN 1 THEN 1 ELSE 0 END ) as 'failure_num', b.old_case_id as 'testcase_id', b.survey_task_id FROM app_case_result as a LEFT JOIN app_case_device AS d ON d.id = a.execute_id LEFT JOIN app_case as b ON b.case_id = a.case_id WHERE b.survey_task_id = #{surveyTaskId, jdbcType=INTEGER}
AND d.province like concat('%',#{province, jdbcType=VARCHAR},'%')
GROUP BY b.old_case_id ) as a ) as b on b.testcase_id = c.old_case_id LEFT JOIN ( SELECT c.old_case_id as 'testcase_id', AVG( click_num ) as 'avg_click_num', AVG( input_num ) as 'avg_input_num', AVG( consume_time ) / 1000 as 'avg_consume_time', avg( ( SELECT avg( consume_time ) FROM app_record_step WHERE command = 'smsVerify' GROUP BY case_result_id HAVING case_result_id = r.result_id ) ) / 1000 as 'avg_msg_consume_time' -- 短信送达耗时 FROM app_case_result AS r LEFT JOIN app_case_device AS d ON d.id = r.execute_id LEFT JOIN app_case as c on c.case_id = r.case_id WHERE r.execute_result = 0 AND c.survey_task_id = #{surveyTaskId, jdbcType=INTEGER}
AND d.province like concat('%',#{province, jdbcType=VARCHAR},'%')
GROUP BY c.old_case_id HAVING c.old_case_id != 0 ) as a on a.testcase_id = b.testcase_id WHERE 1=1 AND c.survey_task_id = #{surveyTaskId, jdbcType=INTEGER} ) as a WHERE 1=1
AND a.testcase_id IN
#{caseId}

SELECT a.*, IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 2 AND ( a.avg_click_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_click_num <= CAST( `end` AS DECIMAL) ) LIMIT 1 ),100) as 'click_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 3 AND ( a.avg_input_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_input_num <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'input_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 4 AND ( a.avg_msg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_msg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'msg_consume_time_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.testcase_id AND survey_task_id = a.survey_task_id AND type = 1 AND ( a.avg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'consume_time_score', success_rote AS 'score', "1" as 'source' FROM ( SELECT c.old_case_id as 'testcase_id', c.case_name as 'testcase_name', c.survey_task_id, b.province, (0+CAST(IFNULL(a.avg_click_num, 0) AS CHAR)) as 'avg_click_num', (0+CAST(IFNULL(a.avg_input_num, 0)AS CHAR)) as 'avg_input_num', (0+CAST(IFNULL(a.avg_msg_consume_time, 0)AS CHAR)) as 'avg_msg_consume_time', (0+CAST(IFNULL(a.avg_consume_time, 0)AS CHAR)) as 'avg_consume_time', (0+CAST(IFNULL(b.success_rote, 0)AS CHAR)) as 'success_rote' FROM app_case as c LEFT JOIN ( SELECT a.success_num / ( a.success_num + a.failure_num ) * 100 as 'success_rote', a.testcase_id, a.province, survey_task_id FROM ( SELECT SUM( CASE a.execute_result WHEN 0 THEN 1 ELSE 0 END ) as 'success_num', SUM( CASE a.execute_result WHEN 1 THEN 1 ELSE 0 END ) as 'failure_num', d.province, b.old_case_id as 'testcase_id', b.survey_task_id FROM app_case_result as a LEFT JOIN app_case_device AS d ON d.id = a.execute_id LEFT JOIN app_case as b ON b.case_id = a.case_id WHERE b.survey_task_id = #{surveyTaskId, jdbcType=INTEGER} GROUP BY b.old_case_id, d.province ) AS a ) AS b ON b.testcase_id = c.old_case_id LEFT JOIN ( SELECT c.old_case_id as 'testcase_id', d.province, AVG( click_num ) as 'avg_click_num', AVG( input_num ) as 'avg_input_num', AVG( consume_time ) / 1000 as 'avg_consume_time', AVG( ( SELECT AVG( consume_time ) FROM app_record_step WHERE command = 'smsVerify' GROUP BY case_result_id HAVING case_result_id = r.result_id ) ) / 1000 as 'avg_msg_consume_time' -- 短信送达耗时 FROM app_case_result AS r LEFT JOIN app_case_device AS d ON d.id = r.execute_id LEFT JOIN app_case as c on c.case_id = r.case_id WHERE r.execute_result = 0 AND c.survey_task_id = #{surveyTaskId, jdbcType=INTEGER} GROUP BY c.old_case_id, d.province HAVING c.old_case_id != 0 ) AS a ON ( a.testcase_id = b.testcase_id AND a.province= b.province ) WHERE 1=1 AND c.survey_task_id = #{surveyTaskId, jdbcType=INTEGER} AND b.province IS NOT NULL ) as a WHERE 1=1
AND a.testcase_id IN
#{caseId}

SELECT DISTINCT (execute_num) AS excuteNum FROM app_case_device AS cd LEFT JOIN app_case AS c ON cd.case_id=c.case_id WHERE execute_num != 0 AND c.sys_task_id IS NULL AND c.survey_task_id IS NULL
and c.old_case_id = #{caseId,jdbcType=INTEGER}
AND cd.execute_status=2 ORDER BY execute_num DESC

