mapper.select:
SELECT app_case_result.result_id as testCaseReportId, business.name AS businessName, business.id , project.`name` as projectName, app_case.case_name AS testcastName, app_case_device.id, app_case_device.province, app_case_device.tel_num AS phoneNum, app_case_device.execute_time AS excuteTime, app_device.device_brand as executeHost, (select sys_user.name from sys_user where sys_user.id =app_case.update_person ) AS execMan, app_case_result.pass_status AS passStatus, app_case_device.pass_status AS excuteResult, app_case_result.result_id AS resultId , app_case_result.log_url AS log, app_case_result.defect_desc AS defectDescription, app_case_result.screenshot_url AS snapshotLocation, app_case_result.record_video_url AS videoLocation, 'app' as type , app_case_device.execute_num as excuteNum FROM app_case_result left JOIN app_case_device ON app_case_result.execute_id = app_case_device.id JOIN app_case ON app_case.case_id = app_case_device.case_id LEFT JOIN app_device ON app_case_device.device_id = app_device.device_id LEFT JOIN business ON app_case.business_id = business.id LEFT JOIN app_script ON app_case.script_id = app_script.script_id LEFT JOIN (SELECT * FROM app_case WHERE old_case_id = 0
AND case_name LIKE CONCAT('%', #{testcastName},'%')
) h ON app_case.old_case_id = h.case_id left JOIN project ON business.project_id=project.id WHERE app_case.sys_task_id IS NULL
AND app_case.survey_task_id IS NULL
AND business.name LIKE CONCAT('%', #{businessName},'%')
AND app_case_device.province LIKE CONCAT('%',#{province},'%')
AND app_case.case_name LIKE CONCAT('%',#{testcastName},'%')
AND app_case_result.pass_status = #{excuteResult}
AND app_case.old_case_id = #{testcastId}
AND app_case_device.pass_status = #{passStatus}
AND app_case_device.tel_num LIKE CONCAT('%',#{phoneNum},'%')
and app_case_result.result_id= #{testCaseReportId}
AND app_case_device.execute_num = #{excuteNum}
AND app_case.survey_task_id = #{surveyTaskId}
AND app_case.survey_task_id IS NULL
AND project.`name` LIKE CONCAT('%', #{projectName},'%')
AND app_case_result.result_id IN
#{testCaseReportId,jdbcType=INTEGER}

SELECT business.name AS businessName, app_case_result.result_id as testCaseReportId, business.id , project.`name` as projectName, app_case.case_name AS testcastName, app_case_device.id, app_case_device.province, app_case_device.execute_num as executeNum, app_case_device.tel_num AS phoneNum, app_device.device_brand AS deviceBrand, (select sys_user.name from sys_user where sys_user.id =app_case.update_person ) AS execMan, app_case_device.execute_time AS executeTime, app_device.device_brand, app_case_device.pass_status AS passStatus, app_case_result.pass_status AS excuteResult, app_case_result.log_url AS log, app_case_result.defect_desc AS defectDescription, app_case_result.screenshot_url AS snapshotLocation, app_case_result.record_video_url AS videoLocation, app_script.script_type FROM app_case_result LEFT JOIN app_case_device ON app_case_result.execute_id = app_case_device.id LEFT JOIN app_case ON app_case.case_id = app_case_device.case_id LEFT JOIN app_device ON app_case_device.device_id = app_device.device_id LEFT JOIN business ON app_case.business_id = business.id LEFT JOIN app_script ON app_case.script_id = app_script.script_id LEFT JOIN project ON business.project_id=project.id WHERE app_case.sys_task_id IS NULL
AND app_case.survey_task_id IS NULL
AND business.name LIKE CONCAT('%', #{businessName},'%')
AND app_case_device.province LIKE CONCAT('%',#{province},'%')
AND app_case.case_name LIKE CONCAT('%',#{testcastName},'%')
AND app_case_device.execute_status = #{executeStatus}
AND app_case_device.pass_status = #{passStatus}
AND app_case_device.execute_num = #{executeNum}
AND app_case_device.execute_num = (SELECT Max(execute_num) FROM app_case_device WHERE case_id = #{caseId})
AND app_case_device.tel_num LIKE CONCAT('%',#{phoneNum},'%')
AND app_case.old_case_id = #{testcastId}
and app_case.old_case_id=#{caseId}
and app_case_result.pass_status=#{excuteResult}
and app_case_result.result_id=#{testCaseReportId}
AND app_case_result.result_id IN
#{testCaseReportId,jdbcType=INTEGER}

SELECT business.name AS businessName, business.id , app_case_device.device_id AS deviceId, app_script.script_name AS scriptName, app_case.case_name AS testcastName, (select sys_user.name from sys_user where sys_user.id =app_case.update_person ) AS execMan, app_case_device.id as executeId, app_case_device.province, app_case_device.tel_num AS phoneNum, app_case_device.execute_time AS executeTime, app_device.device_brand AS deviceBrand, app_case_device.pass_status AS executeStatus, app_case_device.execute_status AS passStatus, app_script.script_type FROM app_case JOIN app_case_device ON app_case.case_id = app_case_device.case_id JOIN app_device ON app_case_device.device_id = app_device.device_id JOIN business ON app_case.business_id = business.id JOIN app_script ON app_case.script_id=app_script.script_id WHERE app_case.sys_task_id IS NULL
AND app_case.survey_task_id IS NULL
and app_case.old_case_id =#{caseId,jdbcType=INTEGER}
and app_script.script_name LIKE CONCAT('%',#{scriptName,jdbcType=VARCHAR},'%')
AND app_case_device.province LIKE CONCAT('%',#{province,jdbcType=VARCHAR},'%')
AND app_case_device.pass_status =#{executeStatus,jdbcType=INTEGER}
AND app_case_device.execute_status =#{passStatus,jdbcType=INTEGER}
ORDER BY app_case_device.id DESC

SELECT rs.case_result_id as testCaseReportId, c.case_name testcastName, '1' as type , (SELECT script_name FROM app_script as script WHERE script.script_id = c.script_id ) as scriptName, cd.execute_num as excuteNum, cd.tel_num as phoneNum, rs.step_no as step, rs.step_desc as remark , rs.consume_time as consumeTime, rs.result as stepResult, rs.screenshot_url as screenShotUrl, rs.command as paramName FROM app_record_step as rs LEFT JOIN app_case_result as r on r.result_id = rs.case_result_id LEFT JOIN app_case as c on c.case_id = r.case_id LEFT JOIN app_case_device as cd on ( cd.case_id = c.case_id AND cd.device_id = r.device_id)
and cd.execute_num=#{excuteNum}
and c.case_name LIKE CONCAT('%',#{testcastName},'%')
and cd.tel_num LIKE CONCAT('%',#{phoneNum},'%')
and r.result_id=#{testCaseReportId}

mapper.delete:
delete from app_case_device
where case_id in(select case_id from app_case_result where result_id in

#{testCaseReportId, jdbcType=INTEGER}

)

mapper.select:
SELECT b.step_no as step ,b.action_type as param_name ,a.step_desc as remark,convert(a.avg_consume_time,decimal) avg_consume_time FROM ( SELECT app_record_step.step_no,AVG(app_record_step.consume_time) AS avg_consume_time, app_record_step.step_desc FROM app_record_step LEFT JOIN app_case_result a ON app_record_step.case_result_id = a.result_id LEFT JOIN app_case b ON a.case_id = b.case_id LEFT JOIN app_case_device ON a.execute_id=app_case_device.id WHERE b.old_case_id =#{caseId} AND survey_task_id =#{surveyTaskId, jdbcType=INTEGER} AND a.pass_status = 0
AND app_case_device.province = #{province}
GROUP BY app_record_step.step_no,app_record_step.step_desc ) a LEFT JOIN ( SELECT a.* FROM app_script_step a LEFT JOIN app_case b ON a.script_id = b.script_id WHERE b.case_id = #{caseId} ) b ON a.step_no = b.step_no

select DISTINCT(province) from app_case_result tr LEFT JOIN app_case_device tc on tr.case_id = tc.case_id LEFT JOIN app_case ON app_case.case_id=tc.case_id WHERE app_case.old_case_id in
#{caseId,jdbcType=INTEGER}
and app_case.survey_task_id is NOT NULL

SELECT a.*, IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.case_id AND survey_task_id = a.survey_task_id AND type = 2 AND ( a.avg_click_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_click_num <= CAST( `end` AS DECIMAL) ) LIMIT 1 ),100) as 'click_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.case_id AND survey_task_id = a.survey_task_id AND type = 3 AND ( a.avg_input_num > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_input_num <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'input_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.case_id AND survey_task_id = a.survey_task_id AND type = 4 AND ( a.avg_msg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_msg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'msg_consume_time_score', IFNULL((SELECT score FROM app_survey_task_score WHERE testcase_id = a.case_id AND survey_task_id = a.survey_task_id AND type = 1 AND ( a.avg_consume_time > CAST( `start` AS DECIMAL) OR `start` = 0 ) AND ( `end` IS NULL OR a.avg_consume_time <= CAST( `end` AS DECIMAL) ) LIMIT 1), 100) as 'consume_time_score', success_rote AS 'score', 1 as 'source' FROM ( SELECT c.old_case_id as 'case_id', c.case_name as 'testcase_name', c.survey_task_id, (0+CAST(IFNULL(a.avg_click_num, 0) AS CHAR)) as 'avg_click_num', (0+CAST(IFNULL(a.avg_input_num, 0)AS CHAR)) as 'avg_input_num', (0+CAST(IFNULL(a.avg_msg_consume_time, 0)AS CHAR)) as 'avg_msg_consume_time', (0+CAST(IFNULL(a.avg_consume_time, 0)AS CHAR)) as 'avg_consume_time', (0+CAST(IFNULL(b.success_rote, 0)AS CHAR)) as 'success_rote' FROM app_case as c LEFT JOIN ( SELECT a.success_num / ( a.success_num + a.failure_num ) * 100 as 'success_rote', a.case_id, survey_task_id FROM ( SELECT SUM( CASE a.pass_status WHEN 0 THEN 1 ELSE 0 END ) as 'success_num', SUM( CASE a.pass_status WHEN 1 THEN 1 ELSE 0 END ) as 'failure_num', b.old_case_id as 'case_id', b.survey_task_id FROM app_case_result as a LEFT JOIN app_case_device AS d ON d.id = a.execute_id LEFT JOIN app_case as b on b.case_id = a.case_id WHERE b.survey_task_id = #{surveyTaskId, jdbcType=INTEGER}
AND d.province like concat('%',#{province, jdbcType=VARCHAR},'%')
GROUP BY b.case_id ) as a ) as b on c.old_case_id = b.case_id LEFT JOIN ( SELECT b.old_case_id as 'case_id', AVG( click_num ) as 'avg_click_num',-- 点击数 AVG( input_num ) as 'avg_input_num',-- 输入数 AVG( consume_time ) / 1000 as 'avg_consume_time',-- 总耗时 avg( ( SELECT avg( consume_time ) FROM app_record_step WHERE command = 'smsVerify' GROUP BY case_result_id HAVING case_result_id = a.result_id ) ) / 1000 as 'avg_msg_consume_time' -- 短信送达耗时 FROM app_case_result a LEFT JOIN app_case_device AS d ON d.id = a.execute_id LEFT JOIN app_case as b on b.case_id = a.case_id WHERE a.pass_status = 0 AND b.survey_task_id = #{surveyTaskId, jdbcType=INTEGER}
AND d.province like concat('%',#{province, jdbcType=VARCHAR},'%')
GROUP BY b.old_case_id HAVING b.old_case_id != 0 ) a on a.case_id = b.case_id WHERE 1=1 AND c.survey_task_id = #{surveyTaskId, jdbcType=INTEGER} ) a WHERE 1=1
AND a.case_id IN
#{caseId}

SELECT e.NAME AS 'businessName', p.NAME AS 'projectName', a.case_id AS 'copyCaseId', b.case_id AS 'caseId', b.case_num AS 'serialNumber', b.case_name AS 'caseName', c.execute_num AS 'executeNum', c.tel_num AS 'telNum', c.province AS 'province', dev.device_brand AS 'deviceHost', d.pass_status AS 'passStatus', d.execute_result AS 'executeResult', c.execute_time AS 'executeTime', d.result_id AS 'caseResultId', d.is_finish AS 'isFinish', d.log_url AS 'logUrl', d.screenshot_url AS 'screenshotUrl', d.record_video_url AS 'recordVideoUrl', d.pass_status AS 'passStatus', d.defect_desc AS 'defectDesc', f.script_name AS 'scriptName', "app" AS 'type', s.id, s.step_no AS 'stepNo', s.result, s.screenshot_url AS 'stepShotUrl', s.command FROM ( SELECT * FROM app_case WHERE old_case_id != 0 ) AS a LEFT JOIN ( SELECT * FROM app_case WHERE old_case_id = 0
AND case_name LIKE CONCAT('%', #{caseName},'%')
) AS b ON b.case_id = a.old_case_id LEFT JOIN app_case_device AS c ON c.case_id = a.case_id LEFT JOIN app_device AS dev ON dev.device_id = c.device_id LEFT JOIN app_case_result AS d ON d.execute_id = c.id LEFT JOIN business AS e ON e.id = b.business_id LEFT JOIN project AS p ON p.id = e.project_id LEFT JOIN app_script AS f ON f.script_id = b.script_id LEFT JOIN app_record_step AS s ON s.case_result_id = d.result_id WHERE 1=1 AND d.result_id IS NOT NULL
AND e.NAME LIKE CONCAT('%', #{businessName},'%')
AND b.case_name LIKE CONCAT('%', #{caseName},'%')
AND b.case_id = #{caseId}
AND c.province LIKE CONCAT('%',#{province},'%')
AND d.execute_result = #{executeResult}
AND c.execute_num = #{executeNum}
AND d.result_id IN
#{id}
AND c.tel_num = #{telNum}
