mapper.select:
SELECT

FROM app_proxy


    AND proxy_id = #{proxyId, jdbcType=INTEGER}


    AND proxy_ip = #{proxyIp, jdbcType=INTEGER}


    AND proxy_mac = #{proxyMac, jdbcType=INTEGER}


    AND online_status = #{onlineStatus, jdbcType=INTEGER}

SELECT

FROM app_proxy
WHERE proxy_ip = #{proxyIp, jdbcType=VARCHAR} AND proxy_mac = #{proxyMac, jdbcType=VARCHAR}

mapper.update:
UPDATE app_proxy

  online_status = #{onlineStatus, jdbcType=VARCHAR}

WHERE proxy_ip = #{proxyIp, jdbcType=VARCHAR} AND proxy_mac = #{proxyMac, jdbcType=VARCHAR}

update app_proxy
set proxy_ip = #{proxyIp,jdbcType=VARCHAR},
  proxy_mac = #{proxyMac,jdbcType=VARCHAR},
  online_status = #{onlineStatus,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where proxy_id = #{proxyId,jdbcType=INTEGER}
