mapper.select:
SELECT 
FROM app_script_step


    AND script_step_id = #{scriptStepId, jdbcType=INTEGER}


    AND script_file_id = #{scriptFileId, jdbcType=INTEGER}


    AND script_id = #{scriptId, jdbcType=INTEGER}


    AND step_no = #{stepNo, jdbcType=VARCHAR}


    AND action_type = #{actionType, jdbcType=VARCHAR}

SELECT 
FROM app_script_step


    AND script_step_id = #{scriptStepId, jdbcType=INTEGER}


    AND script_file_id = #{scriptFileId, jdbcType=INTEGER}


    AND script_id = #{scriptId, jdbcType=INTEGER}


    AND script_id = ( SELECT script_id FROM app_case WHERE case_id = #{appCaseId, jdbcType=INTEGER})


    AND action_type = #{actionType, jdbcType=VARCHAR}


LIMIT 1

